class crypto extends Component {

    state = {
        renderedResponse: null
    }



    async componentDidMount() {
        const response = await fetch('/stock/value?function=CURRENCY_EXCHANGE_RATE&from_currency=BTC&to_currency=INR');
        const data = await response.json;
        console.log(data);
        this.setState({ renderedResponse: data.results[0] })
    }
    hadleSubmit = (event) => {
        console.log("Inside handle submit");
        event.preventDefault();
    }

    render() {
        const renderedResponse = this.state;
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h1>Crypto Values</h1><br />
                    Crypto Code:<select value="crypto">
                        <option value="BTC">BitCoin</option>
                        <option value="LTC">LiteCoin</option>
                        <option value="DOGE">Doge</option>
                    </select><br />
                    Currency: <select value="currency" onChange={this.getResponse}>
                        <option value="INR">Rupee</option>
                        <option value="USD">US Dollar</option>
                    </select> <br /> <br />
                </form>

            </div >
        )
    };

}
export default crypto;
