import React from 'react';

function App() {

  const fetchAndLog = async () => {
    const response = await fetch('http://localhost:5000/stock/value?function=CURRENCY_EXCHANGE_RATE&from_currency=BTC&to_currency=INR');
    const json = await response.json();
    // just log ‘json’
    console.log(json);
  };

  return (
    <div className="App">

      <table>
        <thead>Crypto Values</thead>
        <tbody>
          <th align="right">Crypto Code</th>
          <input name="crypto_name"></input>


          <th align="right">Currency</th> <input name="currency"></input><br />


          <button onClick={fetchAndLog.response}>Submit</button>
          <input type="button" value="Cancel" name="Cancel"></input>

        </tbody>
      </table>
    </div>
  );
}

export default App;
