import React, { Component } from 'react';


class Crypto extends Component {
    state = {
        renderedResponse: ''
    }
    getResponse = async () => {
        console.log("before API call ");
        const response = await fetch('/stock/value?function=CURRENCY_EXCHANGE_RATE&from_currency=BTC&to_currency=INR');
        console.log({ ...response });
        return response.json();
    };
    componentDidMount() {
        this.getResponse().then(res => {
            const data = res;
            this.setState({ renderedResponse: data });
        })
    }

    render() {
        renderedResponse = this.renderedResponse;
        return (
            <div>
                {/* Crypto Values
                Crypto Code:
                <input name="crypto_name"></input>
                Currency: <input name="currency"></input> <br />
                <button onClick={this.getResponse}>Submit</button>
                <input type="button" value="Cancel" name="Cancel"></input> */}
                <p>{renderedResponse}</p>
            </div>
        )
    };

}
export default Crypto;
