import React, { Component } from 'react';


class crypto extends Component {

    state = {
        renderedResponse: null
    };



    async componentDidMount() {
        const url = 'https://api.randomuser.me';
        // const url = '/stock/value?function=CURRENCY_EXCHANGE_RATE&from_currency=BTC&to_currency=INR';
        const response = await fetch(url);
        const data = await response.json();
        console.log(data);
        this.setState({ renderedResponse: data.results[0] })
    }
    // hadleSubmit = (event) => {
    //     console.log("Inside handle submit");
    //     event.preventDefault();
    // }

    render() {
        const renderedResponse = this.state;
        return (
            <div>
                {/* <form> */}
                <h1>Crypto Values</h1><br />
                Crypto Code:<select>
                    <option value="BTC">BitCoin</option>
                    <option value="LTC">LiteCoin</option>
                    <option value="DOGE">Doge</option>
                </select><br />
                Currency: <select>
                    <option value="INR">Rupee</option>
                    <option value="USD">US Dollar</option>
                </select> <br /> <br />
                {/* </form> */}
                <div> {this.state.renderedResponse.name.first}</div>
            </div >
        )
    };

}
export default crypto;
