import React, { Component } from 'react';


class crypto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            [renderedResponse]: null
        }
    }

    getResponse = (event) => {
        event.preventDefault()
        console.log("before API call " + event);
        const response = fetch('/stock/value?function=CURRENCY_EXCHANGE_RATE&from_currency=BTC&to_currency=INR');
        this.setState({ [renderedResponse]: response })
        //return response.json();
    };
